using UnityEngine;public class PlayerCamera : MonoBehaviour {    private const float CameraY = 1.5f;    private const float CameraZ = 2.5f;    private const float CameraMinY =  1.0f;    private const float CameraMaxY = 10.0f;    [SerializeField]    private Camera camera_ = null;    [SerializeField]    private Transform player_ = null;    private Transform camera_transform_ = null;    private Vector3 distance_ = Vector3.zero;    void Start() {        camera_transform_ = camera_.transform;        distance_ = new Vector3(0.0f, CameraY, CameraZ);    }    void LateUpdate() {        float v = Input.GetAxis("Vertical");        if (v != 0.0f) {            Vector3 zoom = distance_ * (1.0f + v);            if (zoom.y >= CameraMinY && zoom.y <= CameraMaxY) {                distance_ = zoom;            }        }        float h = Input.GetAxis("Horizontal");        if (h != 0.0f) {            distance_ = Quaternion.AngleAxis(h * -3.0f, Vector3.up) * distance_;        }        SetPotion();    }    private void SetPotion() {        camera_transform_.position = player_.position + distance_;        camera_transform_.LookAt(player_);    }}